from .distributions.Exponential import ExponentialClass
server_list = [s1,s2,s3]
arrival_dist = ExponentialClass(lambdaValue=1.2)

sim = Simulation(server_list=server_list, arrival_distribution_instance=arrival_dist)
df=pd.DataFrame(columns=['Average interarrival time','Average service time server1','Average service time server 2','Average service time server 3','Utilization teller 1','Utilization teller 2','Utilization teller 3','People who had to wait in line','Total average wait time','Lost Customers'])

for i in range(100):
    np.random.seed(i)
    sim.__init__(server_list=server_list,arrival_distribution_instance=arrival_dist)
    while sim.clock <= 240 :
        sim.time_adv() 
    a=pd.Series(
        [
            sim.clock/sim.num_arrivals,
            sim.server_list[0].service_time_sum/sim.server_list[0].num_of_departures,
            sim.server_list[1].service_time_sum/sim.server_list[1].num_of_departures,
            sim.server_list[2].service_time_sum/sim.server_list[2].num_of_departures,
            sim.server_list[0].num_of_departures / sim.clock,
            sim.server_list[1].num_of_departures / sim.clock,
            sim.server_list[2].num_of_departures / sim.clock,
            sim.number_in_queue,
            sim.total_wait_time,
            sim.lost_customers
        ],  
        index=df.columns)
        
    df=df.append(a,ignore_index=True)   
    
df.to_excel('results.xlsx')
